jQuery( document ).ready( function( $ ) {

    function scrollToAnchor(selector){
        let destination = $('#' + selector);

        console.log( 'Offsets: #anwelte (' + $('#anwaelte').offset().top +') #schwerpunkte (' + $('#schwerpunkte').offset().top +') #kontakt (' + $('#kontakt').offset().top +')');
        console.log( 'Next offset: ' + destination.offset().top );

        $( '#main' ).animate({
          scrollTop: destination.offset().top
        }, 2500, 'easeInOutExpo');
    }


    $( '.wp-block-navigation a.wp-block-navigation-item__content' ).on( 'click', function(e) {
        let full_url = this.href;
        let parts    = full_url.split( '#' );

        if( parts.length == 2 ) {
            let anchor = parts[1];

            e.preventDefault();
            scrollToAnchor( parts[1] );
            console.log( 'Scroll to ' + anchor );
        }
    } );


    $(window).on( 'load', function() {
        let anchor = window.location.hash.replace('#', '');

        scrollToAnchor(anchor);
        console.log( 'Scroll to ' + anchor );
    });

});
